import { Spacer, Card, Text, Input, Button, Modal, Row,Grid } from "@nextui-org/react";
import React from "react";
export default function Intervalo_de_confianza() {
    const [visible, setVisible] = React.useState(false);
    const handler = () => setVisible(true);
    const closeHandler = () => {
        setVisible(false);
        console.log("closed");
    };
    const list = [
        {
            title: "Orange",
            img: "/images/fruit-1.jpeg",
            price: "$5.50",
        },
        {
            title: "Tangerine",
            img: "/images/fruit-2.jpeg",
            price: "$3.00",
        },
        {
            title: "Cherry",
            img: "/images/fruit-3.jpeg",
            price: "$10.00",
        }
    ];
    return (
        <>
            <Card css={{ mw: "400px" }}>
                <Card.Body>
                    <Text h1 size={40}
                        css={{
                            textGradient: "45deg, $yellow600 -20%, $red600 100%",
                        }}
                        weight="bold">Intervalo de Confianza</Text>
                    <Spacer y={1} />
                    <Input id='media' type="number" labelPlaceholder="Media de la muestra" />
                    <Spacer y={2} />
                    <Input id='desvio' type="number" labelPlaceholder="Desvio" />
                    <Spacer y={2} />
                    <Input id='N' type="number" labelPlaceholder="TamaÃ±o de muestra" />
                    <Spacer y={2} />
                    <Input id='confianza' type="number" labelPlaceholder="Nivel de confianza" />
                    <Spacer y={1} />
                    <Button shadow color="success" auto onClick={handler}>
                        Agregar Valores
                    </Button>
                </Card.Body>
            </Card>
            <Modal
                closeButton
                blur
                aria-labelledby="modal-title"
                open={visible}
                onClose={closeHandler}
            >
                <Modal.Header>
                    <Text id="modal-title" size={18}>
                        Tipo de
                        <Text b size={18}> Datos</Text>
                    </Text>
                </Modal.Header>
                <Modal.Body>
                    <Grid.Container gap={1} justify="flex-start">
                        {list.map((item, index) => (
                            <Grid xs={4} sm={6} key={index}>
                                <Card isPressable>
                                    <Card.Body css={{ p: 0 }}>
                                        <Card.Image
                                            src={"https://nextui.org" + item.img}
                                            objectFit="cover"
                                            width="100%"
                                            height={140}
                                            alt={item.title}
                                        />
                                    </Card.Body>
                                    <Card.Footer css={{ justifyItems: "center" }}>
                                        <Row wrap="wrap" justify="center" align="center">
                                            <Text b>{item.title}</Text>
                                        </Row>
                                    </Card.Footer>
                                </Card>
                            </Grid>
                        ))}
                    </Grid.Container>
                </Modal.Body>
            </Modal>
        </>
    )
}
