import {Input,Table,Spacer} from "@nextui-org/react";
import React from "react";
export default function ModalTipoDeDatos() {
const [valores, setValores] = React.useState([]);
function updateValueMedia(e) {
  setValores(oldArray => [...oldArray, e])
}
  return (
    <div css={{
      Width: "100%",
    }}>

      <Input label="Agregar Dato" placeholder="Ej: 9" type="number" size="md" fullWidth onClick={updateValueMedia}/>
      <Button shadow color="success" auto onClick={handler}>
                        Agregar Valores
                    </Button>
      <Spacer y={2} />
      <Table
      aria-label="Example table with static content"
      css={{
        height: "auto",
        Width: "100%",
      }}
    >
      <Table.Header>
        <Table.Column>Xi</Table.Column>
        <Table.Column>Xi2</Table.Column>
      </Table.Header>
      <Table.Body>
        {
          valores.map((item, index) => (
          <Table.Row key={index}>
          <Table.Cell>{item.valor}</Table.Cell>
          <Table.Cell>{Math.pow(item.valor, 2)}</Table.Cell>
        </Table.Row>
        ))}
      </Table.Body>
    </Table>
    </div>
  );
}
